import styled from 'styled-components';

export const MenuButtonWrapper = styled.button`
    position: relative;
    z-index: 10;
    cursor: pointer;
    display:block;
    height: 100%;
    width: 100%;
    padding: 0;
    border: none;

    &:hover {
        > div:first-of-type:before {
            transform: scale(1.2);
            transition: all 0.3s;
        }
    }
`;
