import React from 'react';
import { makeStyles, createStyles, Theme } from "@material-ui/core/styles";
import Paper from "@material-ui/core/Paper";
import Grid from "@material-ui/core/Grid";
import { FullBackgroundImage } from './components/FullBackgroundImage/full-background-image.component'

const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    root: {
      flexGrow: 1
    },
    paper: {
      padding: theme.spacing(0),
      height: "100%",
      textAlign: "center",
      color: theme.palette.text.secondary,
      boxSizing: "border-box"
    },
    gridItem: {
      boxSizing: "border-box",
      padding: theme.spacing(0)
    },
    masonryItem: {
      boxSizing: "border-box",
      padding: `0 ${theme.spacing(0)}px ${theme.spacing(0)}px ${theme.spacing(0)}px`,
      [theme.breakpoints.up("lg")]: {
        padding: `0px ${theme.spacing(0)}px 0px 0px`
      },
      [theme.breakpoints.down("sm")]: {
        padding: `0px ${theme.spacing(0)}px 0px 0px`
      }
    }
  })
);


function App() {
  const classes = useStyles();
  return (
    <Grid container spacing={0} style={{ height: "100%" }}>
      <Grid item md={6} xs={12} className={`${classes.gridItem}`}>
        <Paper elevation={0} className={`${classes.paper}`}>
        <FullBackgroundImage srcSet='/img/danielle_frappier_bg.jpg'>
          <h1>Hi there!<br/> My name is <span>Danielle</span></h1>
        </FullBackgroundImage>
        </Paper>
      </Grid>
      <Grid item container md={6} xs={12} className={classes.gridItem}>
        <Grid
          item
          lg={12}
          md={12}
          xs={12}
          className={`${classes.masonryItem}`}
        >
          <Paper elevation={0} className={classes.paper}>
            <FullBackgroundImage srcSet='https://www.daniellefrappier.com/images/danielle_frappier_bg.jpg'>
              Profile
            </FullBackgroundImage>
          </Paper>
        </Grid>
        <Grid
          item
          lg={12}
          md={12}
          xs={12}
          className={`${classes.masonryItem}`}
        >
          <Paper elevation={0} className={classes.paper}>Shared</Paper>
        </Grid>
        <Grid
          item
          lg={12}
          md={12}
          xs={12}
          className={`${classes.masonryItem}`}
        >
          <Paper elevation={0} className={classes.paper}>Shared</Paper>
        </Grid>
        <Grid
          item
          lg={12}
          md={12}
          xs={12}
          className={`${classes.masonryItem}`}
        >
          <Paper elevation={0} className={classes.paper}>Shared</Paper>
        </Grid>
      </Grid>
  </Grid>
  );
}

export default App;
