import React, { useState } from 'react';

type ActionSectionType = {
  currentSection: string;
  setCurrentSection: React.Dispatch<React.SetStateAction<string>>;
};

const Context = React.createContext<ActionSectionType>({} as ActionSectionType);

const currentSectionContextProvider: React.FC = ({ children }) => {
  const [currentSection, setCurrentSection] = useState('home');

  return (
    <Context.Provider value={{ currentSection, setCurrentSection }}>
      {children}
    </Context.Provider>
  );
};

export default currentSectionContextProvider;

export const currentSectionContext = () => React.useContext(Context);
