import React, { useState } from 'react';

type ActionSectionType = {
  activeSection: string;
  setActiveSection: React.Dispatch<React.SetStateAction<string>>;
};

const Context = React.createContext<ActionSectionType>({} as ActionSectionType);

const ActiveSectionContextProvider: React.FC = ({ children }) => {
  const [activeSection, setActiveSection] = useState('home');

  return (
    <Context.Provider value={{ activeSection, setActiveSection }}>
      {children}
    </Context.Provider>
  );
};

export default ActiveSectionContextProvider;

export const ActiveSectionContext = () => React.useContext(Context);
