import React, { createContext, useState  } from 'react';
export const ActiveSectionContext = createContext<any>('home');

const ActiveSectionContextProvider = (props: any) => {
    const [currentSection, setCurrentSection] = useState(ActiveSectionContext);

    return (
        <ActiveSectionContext.Provider value={{currentSection, setCurrentSection}}>
            {props.children}
        </ActiveSectionContext.Provider>
    );
 }
 export default ActiveSectionContextProvider;
