import React from 'react';
import { 
    IntroContentWrapper
 } from './intro-content.style';
import { IntroContentProps } from './into-content.props'

export const IntroContent = ( {
    isMenuItem = false,
    children,
    ...restProps
}: IntroContentProps) => {
    const openSection = () => {
        console.log("something should happen here");
        const main = document.getElementById('main');
        const menu = document.getElementById('menu');

        if (main && menu !== null) {
            main.style.visibility = 'hidden';
            menu.style.visibility = 'hidden';
        }


    }
    return (
        <IntroContentWrapper onClick={ isMenuItem ? openSection : undefined } {...restProps}>{children}</IntroContentWrapper>

    );
}
